const Header = (props) => {

  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )

}

const Part = ({p, t}) => {
  console.log('tämän saa Part', p, t)

  return(
    <div>
      <p>{p} - {t}</p>
    </div>
  )
}

// objekti tarvii propseihin ({}) sulkeet
const Content = ({parts}) => {


  return(
    <div>
        <Part p = {parts[0].name} t = {parts[0].exercises} /> 
        <Part p = {parts[1].name} t = {parts[1].exercises}/> 
        <Part p = {parts[2].name} t = {parts[2].exercises}/>
    </div>
  )

}

const Total = ({parts}) => {
  console.log('total saa tämän', parts[0].exercises)
 const total = parts[0].exercises + parts[1].exercises + parts[2].exercises

  return(
    <div>
      <p>Total:  {total}</p>
    </div>
  )
}


const App = () => {
  const course = {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
      <Course course={course} />
    </div>
  )
}

export default App
