const Header = (props) => {

  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )

}

const Content = (props) => {
  console.log(props)


  return(
    <div>
        <Part part1 = {props[0]}/> 
        <Part part2 = {props[1]}/> 
        <Part part3 = {props[2]}/>
    </div>
  )

}

const Total = (props) => {

  return(
    <div>
      <p>Total: {props.total}</p>
    </div>
  )
}


const App = () => {
  const course = 'Half Stack application development'

  const parts = [
    {name:'Fundamentals of React',
   exercise1s: 10},

  {name: 'Using props to pass data',
  exercises:  7},

  {name: 'State of a component',
  exercises: 13}
  ]
  console.log(parts.exercises[0])
  const total = parts.exercises[0] + parts.exercises[1] + parts.exercises[3]

return (
  <div>
    <Header course= {course}/>
    <Content parts= {parts} />
    <Total total = {total}/>
  </div>
)

}

export default App;
