import { useState } from "react"
import '../styles.css';


const Blog = ({ blog, user, addLike, removeBlog }) => {
  const [showInfo, setShowInfo] = useState(false)
 

  

  console.log(user, "t채ss채 user")
  const toggle = () => {
    setShowInfo(!showInfo)
  }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const handleLike = () => {
    const updatedBlog = {
      ...blog,
      likes: blog.likes += 1
    }
    addLike(blog.id, updatedBlog)
  }

  const handleRemove = () => {
    if (window.confirm(`Remove blog ${blog.title} by ${blog.author}?`)) removeBlog(blog.id)
  }
  
  

  return (
    <div style={blogStyle} className="blogInfo">
      {!showInfo && <div>{blog.title} <button onClick={toggle}>show</button></div>
      }
      {showInfo &&
        <div>
          <p>{blog.title}</p>
          <p>{blog.author}</p>
          <p> <a
            href={blog.url}
            style={{
              color: "blue",
              textDecoration: "underline",
            }}
            target="_blank"
            rel="noopener noreferrer"
          >
            {blog.url}
          </a></p>
          <p>{blog.likes}<button onClick={handleLike}>like</button></p>
          <div>
  {blog.user && blog.user.username ?
    <p>{blog.user.username}</p>
    : <p>No username</p>
  }
</div>

          <button onClick={toggle}>hide</button>
          <div>{blog.user && blog.user.username === user ?
  <button onClick={handleRemove}>remove</button>
  : ''}</div>

        </div>
      }

    </div>
  )
}

export default Blog
