    <div>
      <p>{user.name} has logged in</p>
      <button onClick={handleLogout}>Log out</button>
      <h2>Create New</h2>
      <form onSubmit={handleCreate}>
      Title:
      <input 
      type="title"
      value={newBlog.title}
      name="Title"
      ></input>
      Author:
      <input 
      type="author"
      value={newBlog.author}
      name="Author"
      ></input>
      URL:
      <input 
      type="url"
      value={newBlog.url}
      name="url"
      ></input>
      <button type="submit">create</button>
      </form>
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
        
      )}
    </div>
  )

  return (
    <div>
      <h1>Blogs</h1>
      {!user && loginForm()}
      {user && blogForm()}
    </div>
  )
}

export default App
