import { useState, useEffect, useRef } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import './styles.css';
import Alert from '@mui/material/Alert';
import Togglable from './components/togglable';
import BlogForm from './components/blogForm';



const App = () => {
  const [blogs, setBlogs] = useState([])
  const [errorMessage, setErrorMessage] = useState(null)

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [successVisible, setSuccessVisible] = useState(false)

  const blogFormRef = useRef()

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()

    try {
      const user = await loginService.login({
        username, password,
      })

      // istunto ei keskeydy vaikka uudelleenladataan sivu
      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )

      // tällä voit kirjautua ulos: window.localStorage.removeItem('loggedNoteappUser')

      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage('wrong credentials')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
    console.log('logging in with', username, password)
  }

  const handleLogout = () => {
    window.localStorage.removeItem('loggedBlogappUser')
    setUser(null)
  }


  const loginForm = () => (
    <form onSubmit={handleLogin}>
      <div>
        username
        <input
          type="text"
          value={username}
          name="Username"
          onChange={({ target }) => setUsername(target.value)}
        />
      </div>
      <div>
        password
        <input
          type="password"
          value={password}
          name="Password"
          onChange={({ target }) => setPassword(target.value)}
        />
      </div>
      <button type="submit">login</button>
    </form>
  )

 
  const handleCreate = (newBlog) => {
    blogService.create(newBlog)
      .then(returnedBlog => {
        setBlogs(blogs.concat(returnedBlog))
        setSuccessVisible(true)
        setTimeout(() => {
          setSuccessVisible(false)
        }, 5000)
      })
      .catch(error => {
        // Tässä voit käsitellä mahdollisen virheen, jos luominen epäonnistui
        console.log('Error creating blog:', error);
      });
  }
  


  //kaksi eri errormessage tapaa, MUI ja pelkkä message
  return (
    <div>
      <Togglable buttonLabel='new blog' ref={blogFormRef}>
  <BlogForm createBlog={handleCreate} />
        <BlogForm
          createBlog={handleCreate}
        />
      </Togglable>
      {successVisible && (
        <Alert severity="success">
          New blog added succesfully!
        </Alert>
      )}
      {errorMessage}
      <h1>Blogs</h1>
      {!user && loginForm()}
      {user && BlogForm()}
    </div>
  )
}

export default App
