import { useState } from "react"




const Blog = ({blog, user}) => {
  const [showInfo, setShowInfo] = useState(false)

  console.log(user, "t채ss채 user")
  const toggle = () => {
    setShowInfo(true)
  }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

return (
  <div style={blogStyle}>
    {!showInfo && <div>{blog.title} {blog.author} <button onClick={toggle}>show more</button></div>}
    
  </div>  
)
}

export default Blog
