        event.target.anecdote.value = '';
        dispatch(createAnecdoteAction(content));
}

    */

    return (
        <div>
            <h1>Create New</h1>
            <form onSubmit={create}>
                <div>
                    <input name="anecdote" />
                </div>
                <button type="submit">create</button>
            </form>
        </div>
    )
}

export default AnecdoteForm;
